SELECT stu.student_id, stu.student_name, sub.subject_name, count(e.subject_name) AS attended_exams FROM students stu CROSS JOIN subjects sub LEFT JOIN examinations e ON stu.student_id=e.student_id AND e.subject_name=sub.subject_name GROUP BY stu.student_id, sub.subject_name ORDER BY stu.student_id, sub.subject_name;